import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
  const [enteredYear, setEnteredYear] = useState("2020");

  props.expenses.forEach(element => {
    console.log(element.getFullYear());
  });

  const onYearChange = (year) => {
    setEnteredYear(year);
    console.log(year);
  };

  return (
    <div>
      <Card className="expenses">
        {props.expenses.filter(expense => expense.date.getFullYear() == "2021").map(expenseFilteredByYear => {
          <ExpenseItem
          key={expenseFilteredByYear.id}
          title={expenseFilteredByYear.title}
          amount={expenseFilteredByYear.amount}
          date={expenseFilteredByYear.date}
        />
        })}
      </Card>
    </div>
  );
};

export default Expenses;
